import pyPolyampholyte

def analyze_sequence(sequence):
    abundance = []
    # occurences of the first 20 amino acids in the sequence are
    # counted, needs to be adapted if more than 20 amino acids such
    # as hydroxylysine are added
    for key in pyPolyampholyte.group_properties.amino_acids.index[:20]:
        abundance.append(sequence.count(key))
    # Hyp, Hyl abundances
    abundance.extend(
        (len(pyPolyampholyte.group_properties.amino_acids.index) - len(abundance)) * [0])
    return pd.Series(abundance, index=pyPolyampholyte.group_properties.amino_acids.index)

def combine_chains(chains):
    combined_molar_mass = 0
    combined_n = 0
    combined_hyp = 0
    for curr_chain in chains:
        combined_molar_mass += curr_chain.molar_mass()
        combined_n += curr_chain.n_content() * curr_chain.molar_mass()
        combined_hyp += (
            curr_chain.mass_calc_dataset.at['Hyp', 'mass_percent_residue'] *
            curr_chain.molar_mass())

    combined_n_content = combined_n/combined_molar_mass
    combined_hyp_content = combined_hyp/combined_molar_mass
    
    return [combined_n_content, 1/combined_n_content, combined_molar_mass,
            combined_hyp_content]

